package JavaBasics;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//primitive data types - int, double, char and boolean
		//1. Integer
		int i = 10;
		System.out.println(i);
		i = 20;
		int j = 20;
		
		//2. double
		
		double d = 12.33;
		double d1 = 34.33;
		double d2 = 100;
		
		//3. character: single digit value, should be written within single quote
		
		char c = 'a';
		char c1 = 'A';
		
		//4. boolean
		
		boolean b = true;
		boolean b1 = false;
		
		
		//5. String is a class not a data type
		
		String s = "Hello world";
		String s1 = "This is my first code";
		System.out.println(s1);
		

	}

}
